-- Информация о пользователях (аккаунты) 

DROP TABLE IF EXISTS accounts;
CREATE TABLE accounts (
	id bigint UNSIGNED NOT NULL PRIMARY KEY AUTO_INCREMENT,
	login varchar (100) NOT NULL,
	passwd_hash varchar (250) NOT NULL,
	email varchar (250) NOT NULL, -- нужен при регистрации, поэтому сюда, а не в профиль
	if_confirmed boolean DEFAULT FALSE, -- подтвердить аккаунт перейдя по ссылке в почте
	created_at datetime DEFAULT current_timestamp,
	updated_at datetime DEFAULT current_timestamp ON UPDATE current_timestamp
);

ALTER TABLE accounts ADD CONSTRAINT email_check CHECK (email LIKE "__%@__%.__%");
ALTER TABLE accounts ADD CONSTRAINT login_check CHECK (login RLIKE '^[a-z]+' AND login LIKE "___%"); -- от 3х символов и начинается с буквы

