-- Карма: права и ограничения, связанные с кармой пользователей
DROP TABLE IF EXISTS carma;
CREATE TABLE carma(
	user_id bigint UNSIGNED NOT NULL PRIMARY KEY, -- здесь будет связь 1 к 1
	carma bigint DEFAULT NULL, -- может быть отрицательной
	if_troll boolean AS (carma < -100) STORED, -- у троллей в профиле отображается значок :)
	comment_1_per_week boolean AS (carma < -100) STORED, -- есть ли такое ограничение для пользователя
	comment_1_per_day boolean AS (carma BETWEEN -100 AND -31) STORED,
	comment_1_per_hour boolean AS (carma BETWEEN -30  AND -11) STORED,
	comment_1_per_5_minuts boolean AS (carma BETWEEN -10 AND -1) STORED,
	can_public boolean AS (carma >= 0) STORED, 
	can_stop_advert boolean AS (carma >= 1) STORED,
	can_voite boolean AS (carma >= 5) STORED,
	can_public_in_piar boolean AS (carma >= 30) STORED,
	get_one_invitation boolean AS (carma >= 50) STORED,
	CONSTRAINT user_id_fk_carma FOREIGN KEY (user_id) REFERENCES accounts (id)
);

