-- 6 - скрипты характерных выборок (включающие группировки, JOIN'ы, вложенные таблицы);




-- -- --  1 - найти лучшую статью за последний месяц 


SELECT * FROM 
(SELECT id FROM articles a WHERE TIMESTAMPDIFF(MONTH, created_at, NOW()) <= 1) t1
INNER JOIN 
(SELECT sum(evaluation) raiting, id_article FROM raiting_voiting_articles rva 
GROUP BY id_article ORDER BY raiting desc) t2
ON t1.id = t2.id_article
ORDER BY raiting DESC LIMIT 1;

-- -- -- 2 - вывести рейтинг компаний (нужно вывести их название и рейтинг в порядке убывания)

SELECT name, raiting
FROM company c INNER JOIN 
(SELECT sum(evaluation) raiting, company_id 
FROM raiting_voiting_company_blogs rvcb 
INNER JOIN company_blogs cb 
ON cb.id = rvcb.id_post 
GROUP BY company_id) rait
ON c.id = rait.company_id
ORDER BY raiting desc;

-- -- -- 3 - Составить рейтинг вопросов по популярности: сам вопрос + рейтинг в порядке убывания

-- Популярность определим по просмотрам + ответам + лайкам вопроса

SELECT question, rating 
FROM 
questions q
INNER JOIN 
(SELECT sum(rating) AS rating, question_id from
(SELECT sum(is_liked) AS rating, question_id FROM question_likes ql GROUP BY question_id
UNION ALL 
SELECT count(*) AS rating, question_id FROM q_views qv GROUP BY question_id
UNION ALL 
SELECT count(*) AS rating, question_id FROM answers GROUP BY question_id) T
GROUP BY T.question_id) T2
ON q.id = T2.question_id
ORDER BY rating DESC;


-- -- -- 4 
-- На habr Q есть такая таблица, где для каждой подкатегории вопросов показано, сколько вопросов 
-- было в ней задано. Сделать такую же таблицу для категорий (которых 7). Формат вывода:
-- название категории - количество вопросов

SELECT name, cnt
FROM category_articles ca
INNER JOIN 
(SELECT count(*) cnt, id_category FROM questions q2 INNER JOIN under_category_articles uca 
ON q2.id_under_category = uca.id GROUP BY id_category) T
ON ca.id = T.id_category
ORDER BY cnt desc;

-- -- -- 5 - для каждого пользователя вывести его email и названия всех свежих статей, 
-- которые могут быть ему интересны. Выводить только тех пользователей, которые согласились
-- на рассылку. Интересуют пользователи только с подтвержденным аккаунтом.
-- "свежими" будем считать статьи, вышедшие в течение последних 2х месяцев. 
-- итоговая таблица должна выглядеть так:
-- user_id  email 	name_article
-- 1  		aaaa@aa имя1
-- 1  		aaaa@aa имя2 ------- то есть для 1го id могут быть несколько записей.


SELECT user_id, email, name_article
FROM 
(SELECT user_id, email, under_category_articles_id FROM users_interests ui 
INNER JOIN 
(SELECT id, email FROM accounts a 
INNER JOIN profiles p
ON a.id = p.user_id 
WHERE a.if_confirmed = 1 AND p.wants_get_mailing_interesting_articles = 1) T 
ON ui.user_id = T.id) info_from_profiles
INNER JOIN 
(SELECT id_under_category, name AS name_article FROM articles a 
WHERE timestampdiff(MONTH, created_at, now()) <= 2) info_from_articles
ON info_from_profiles.under_category_articles_id = info_from_articles.id_under_category;


-- -- -- 6 - Вывести имя и фамилию пользователя, который лучше всех отвечает на вопросы.

SELECT name, surname FROM 
(SELECT id, name, surname 
FROM accounts a INNER JOIN profiles p ON a.id = p.user_id) Tprof
INNER JOIN 
(SELECT author_id, sum(raiting) AS raiting
FROM answers a2 INNER JOIN 
(SELECT answer_id, sum(is_liked) AS raiting FROM answers_likes al GROUP BY answer_id) T 
ON a2.id = T.answer_id
GROUP BY author_id) Trait 
ON Tprof.id = Trait.author_id
ORDER BY raiting DESC LIMIT 1;











































